mapper.select:
SELECT * FROM (SELECT row_number() OVER (ORDER BY signup_date DESC) AS idx, m.* FROM tbl_member m
${searchOption} LIKE '%'||#{searchKeyword}||'%'
AND type = 0
AND type = 1
AND type = 2
) WHERE idx BETWEEN #{startRange} AND #{endRange}

mapper.update:
DELETE FROM tbl_member 
WHERE user_id IN

	#{user_id}

mapper.select:
SELECT user_id FROM


		tbl_post WHERE post_no = #{target_no} 


		tbl_comment WHERE comment_no = #{target_no}


		tbl_message WHERE message_no = #{target_no}

SELECT * FROM ( SELECT row_number() OVER (ORDER BY expert_id) AS idx, m.* FROM tbl_expert e INNER JOIN tbl_member m ON(e.expert_id = m.user_id) WHERE m.type != 2
AND ${searchKeyword} LIKE '%'||#{searchKeyword}||'%'
ORDER BY signup_date DESC ) WHERE idx BETWEEN ${startRange} AND ${endRange}

SELECT COUNT(*) FROM ( SELECT row_number() OVER (ORDER BY expert_id) AS idx, m.* FROM tbl_expert e INNER JOIN tbl_member m ON(e.expert_id = m.user_id) WHERE m.type != 2
AND ${searchKeyword} LIKE '%'||#{searchKeyword}||'%'
)

mapper.update:
UPDATE tbl_member SET type = 2
WHERE user_id IN

	#{user_id}
