mapper.select:
SELECT COUNT(*) FROM tbl_post


		${searchOption} LIKE '%'||#{searchKeyword}||'%'


		AND type = ${type}

SELECT * FROM (SELECT row_number() OVER (ORDER BY written_Date DESC) AS idx , p.post_no, p.type, p.user_id, p.user_nickname , p.title ,p.view_count, p.written_date , l.count FROM tbl_post p LEFT OUTER JOIN (SELECT post_no, COUNT(*) "COUNT" FROM tbl_like GROUP BY post_no) l ON(p.post_no = l.post_no)
${searchOption} LIKE '%'||#{searchKeyword}||'%'
AND type = #{type}
) WHERE idx BETWEEN #{startRange} AND #{endRange}

