mapper.select:
SELECT * FROM (SELECT row_number() OVER (ORDER BY r.report_date DESC) AS idx ,r.report_no ,m.user_nickname ,i.report_content ,r.type ,r.yn ,r.report_date, COUNT(*) OVER(PARTITION BY r.type, r.target_no) "COUNT" FROM tbl_report r INNER JOIN tbl_reportinfo i USING(report_title) INNER JOIN tbl_member m USING(user_id)
tbl_post p ON(target_no = post_no) WHERE p.title
tbl_comment c ON(target_no = comment_no) WHERE c.content
tbl_message ON(target_no = message_no) WHERE tbl_message.content
) WHERE idx BETWEEN #{startRange} AND #{endRange}
AND type = #{type}

		SELECT COUNT(*) FROM tbl_report r


   				tbl_post p ON(target_no = post_no) WHERE p.title


   				tbl_comment c ON(target_no = comment_no) WHERE c.content


   				tbl_message ON(target_no = message_no) WHERE tbl_message.content



	   			WHERE r.type = ${type}


	   			AND r.type = ${type}
