mapper.select:
select count(*) 
	from tbl_member
		where user_id = #{id}

select count(*)
	from tbl_member
		where user_nickname = #{nickname}

select count(*) 
	from tbl_member
		where email = #{email}

select user_id
	from tbl_member
		where phone = #{phone}

mapper.update:
update tbl_member
	set user_nickname=#{user_nickname}, phone=#{phone}, email=#{email}, address=#{address}
 	where user_id=#{user_id}
