choose.when:
    			SELECT 
    				pd.product_no, pd.name, to_char(pd.price, '999,999,999') "PRICE",
    				pd.stock, pd.content, pd.image_path, l.type
				FROM (SELECT row_number() OVER (ORDER BY like_date DESC) AS idx, tbl_like.* FROM tbl_like) l
					INNER JOIN tbl_product pd ON(pd.product_no = l.post_no)
				WHERE l.type = #{type}
					AND idx BETWEEN ${startRange} AND ${endRange}
					AND l.user_id = #{user_id}

					AND name LIKE '%'||#{searchKeyword}||'%'


					AND content LIKE '%'||#{searchKeyword}||'%'

choose.otherwise:
    			SELECT *
				FROM (SELECT row_number() OVER (ORDER BY like_date DESC) AS idx, tbl_like.* FROM tbl_like) l
					INNER JOIN tbl_post p ON(p.post_no = l.post_no)
				WHERE l.type = #{type}
					AND idx BETWEEN ${startRange} AND ${endRange}
					AND l.user_id = #{user_id}

					AND ${searchOption} LIKE '%'||#{searchKeyword}||'%'

choose.when:
SELECT COUNT(*) FROM tbl_like l INNER JOIN tbl_product pd ON(pd.product_no = l.post_no) WHERE l.user_id = #{user_id}
AND name LIKE '%'||#{searchKeyword}||'%'
AND content LIKE '%'||#{searchKeyword}||'%'

mapper.select:
    	SELECT * 
    	FROM (SELECT row_number() OVER (ORDER BY written_date DESC) AS idx, tbl_post.* FROM tbl_post) 
    	WHERE idx BETWEEN ${startRange} AND ${endRange}
    		AND user_id = #{user_id} 

			AND ${searchOption} LIKE '%'||#{searchKeyword}||'%'

    	SELECT COUNT(*) FROM tbl_post 
    	WHERE user_id = #{user_id} 

			AND ${searchOption} LIKE '%'||#{searchKeyword}||'%'

    	SELECT * FROM (SELECT row_number() OVER (ORDER BY written_Date DESC) AS idx, tbl_post.* FROM tbl_post)
		    INNER JOIN tbl_category c USING(category_no)
	    WHERE idx BETWEEN #{startRange} AND #{endRange}
			AND type = 2 AND c.parent_group = #{parent_group}

    	SELECT COUNT(*) FROM tbl_post p
		    INNER JOIN tbl_category c USING(category_no)
		WHERE type = 2 AND c.parent_group = #{parent_group}
