mapper.select:
select count(*) from tbl_order where order_time like #{today}


	and order_no like 'POINT%'


	and order_no not like 'POINT%'

select order_no from (select row_number() over (order by order_no desc) r, o.* from tbl_order o where order_no like



			'POINT%'



			'ORD%'



		and user_id like #{user_id}


		and order_time like #{order_time}

) where r=1

SELECT * FROM (SELECT ROW_NUMBER() OVER (ORDER BY order_time DESC) AS idx, o.order_no, o.order_time, to_char(o.cost, '999,999,999') "COST", o.status, op.quantity, p.name, p.price, p.image_path FROM tbl_order o INNER JOIN tbl_payment pay ON(o.order_no = pay.payment_no) INNER JOIN tbl_orderproduct op ON(o.order_no = op.order_no) INNER JOIN tbl_product p ON(op.product_no = p.product_no) WHERE user_id = #{user_id}
AND ${searchOption} LIKE '%'||#{searchKeyword}||'%'
) WHERE idx BETWEEN ${startRange} AND ${endRange}

SELECT COUNT(*) FROM (SELECT * FROM tbl_order o INNER JOIN tbl_payment pay ON(o.order_no = pay.payment_no) INNER JOIN tbl_orderproduct op ON(o.order_no = op.order_no) INNER JOIN tbl_product p ON(op.product_no = p.product_no) WHERE user_id = #{user_id}
AND ${searchOption} LIKE '%'||#{searchKeyword}||'%'
)
